// src/App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalItems, setTotalItems] = useState(0);
  const [showShopMenus, setShowShopMenus] = useState(false);

  const products = [
    {
      id: 1,
      name: 'Apple',
      description: '$3 - $7',
      image: 'data:image/jpeg;base64,', // Replace with your image URL
    },
    {
      id: 2,
      name: 'Orange',
      description: '$3 - $5',
      image: 'data:image/jpeg;base64,', // Replace with your image URL
    },
    {
      id: 3,
      name: 'Banana',
      description: '$0.3 - $0.5',
      image: 'data:image/jpeg;base64,', // Replace with your image URL
    },
    {
      id: 4,
      name: 'pineapple',
      description: '$2 - $5',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRCwTyLc_EtGxnOgFO7cQquyIhOCX5z3a4PMg&usqp=CAU', // Replace with your image URL
    },
    {
      id: 5,
      name: 'graps',
      description: '$4 - $7',
      image: 'data:image/jpeg;base64,', // Replace with your image URL
    },
    {
      id: 6,
      name: 'mango',
      description: '$2 - $4',
      image: 'data:image/jpeg;base64,', // Replace with your image URL
    },
    {
      id: 7,
      name: 'wattermelon',
      description: '$4 - $7',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsy-N23pWbB_I30bVBXS7u_XBjmvFSbWFii2kjH8g-lR-A4DEF0UpCw71JJ0fJCCgU-dc&usqp=CAU', // Replace with your image URL
    },
    {
      id: 8,
      name: 'strawberry',
      description: '$5 - $8',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTMAzdAxvF_VYLeytrNQuecPpSFpDgh5JBW3eALXis-voqboWwddB6rle9_F-v0Yts9X8o&usqp=CAU', // Replace with your image URL
    },
  ];

  const addToCart = (product) => {
    if (!cartItems.find((item) => item.id === product.id)) {
      setCartItems([...cartItems, product]);
      setTotalItems(totalItems + 1);
    }
  };

  const removeFromCart = (product) => {
    const updatedCart = cartItems.filter((item) => item.id !== product.id);
    setCartItems(updatedCart);
    setTotalItems(totalItems - 1);
  };

  const toggleShopMenus = () => {
    setShowShopMenus(!showShopMenus);
  };

  return (
    <div className="App">
      <header>
        <div className="menu">
          <div>Home</div>
          <div>About</div>
          <div onClick={toggleShopMenus}>Shop</div>
          {showShopMenus && (
            <>
              <div>All Products</div>
              <div>Popular Items</div>
              <div>New Arrivals</div>
            </>
          )}

          <div className="cart-info">
          Cart: {totalItems} item(s)
          </div>
        </div>
      </header>
      <div className='title'>
        <h2>Welcome To Our SHOP-KART...!</h2>
      </div>
      <main>
        <section className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              {cartItems.find((item) => item.id === product.id) ? (
                <button onClick={() => removeFromCart(product)}>
                  Remove from Cart
                </button>
              ) : (
                <button style={{backgroundColor:'white', border:'1px solid black',color:'black'}} onClick={() => addToCart(product)}>
                  Add to Cart
                </button>
              )}
            </div>
          ))}
        </section>
      </main>
    </div>
  );
};

export default App;

